mapper.select:
select checkId,count(qdId) as countTeam from team_del
where kfTime >= #{param1} and kfTime <= #{param2}
GROUP BY checkId

select DATE_FORMAT(kfTime,'%Y-%m-%d') as kfTime,count(qdId) as countTeam from team_del
where kfTime >= #{param1} and kfTime <= #{param2}
GROUP BY DATE_FORMAT(kfTime,'%Y-%m-%d')
