mapper.select:
select * from add_point
where userId=#{userId}

    and addDesc=#{addDesc}


    and addPoint=#{addPoint}

order by time desc

select * from delete_point
where userId=#{userId}

    and deleteDesc=#{deleteDesc}


    and deletePoint=#{deletePoint}

order by deleteTime desc

select * from punish
where userId=#{userId}

    and punishReason=#{punishReason}


    and punishComplete=#{punishComplete}


    and punishDesc=#{punishDesc}

order by punishTime desc

select * from reward
where userId=#{userId}

    and rewardReason=#{rewardReason}


    and rewardDesc=#{rewardDesc}

order by rewardTime desc

select * from delete_point
where userId=(select qdbm from user u where u.userId=#{userId})

    and deleteDesc=#{deleteDesc}


    and deletePoint=#{deletePoint}

order by deleteTime desc
