mapper.select:
SELECT
*
FROM
advice
where
1=1

    and title = #{title}


    and isSee = #{isSee}


    and isCross = #{isCross}

order by orderTime

mapper.update:
update from user set
  userName=#{userName},pwd=#{pwd},
  permissionDegree=#{permissionDegree},
  qdbm=#{qdbm},kpfs=#{kpfs}
where
  userId=#{userId}

update user set userId=#{userId}

   ,pwd=#{pwd}


    ,phone=#{phone}


    ,email=#{email}

where
  userId=#{userId}
